mapper.select:
select id, contraseña, nombre, rol, area
from usuario u
where u.contraseña = #{id};

select id, contraseña, nombre, rol, area
from usuario u
order by id;

select id
from usuario
where nombre=#{nombre} and contraseña=#{contraseña};

select *
from usuario
where nombre=#{nombre} and contraseña=#{contraseña}
